import {React , useState} from "react";
import { isMobile } from 'react-device-detect';
import Estilo from "../Hojas de Estilos/Menu.css";
function Menu({ activo }){
        const definirMenu = ()=>{
            let clase;
            if(isMobile){
                clase=activo ? "menu activo" : "menu";
            }else{
                clase="menu laptop";
            }
            return clase;
        }
        const [linkActivo, setLinkActivo] = useState(false);
    return(
        <div className={definirMenu()}>
                <a className={linkActivo? "item-menu activo" : "item-menu" } onClick={()=>{setLinkActivo(true)}}>Inicio</a>
                <a className={linkActivo? "item-menu activo" : "item-menu" } onClick={()=>{setLinkActivo(true)}}>Sobre Nosotros</a>
                <a className={linkActivo? "item-menu activo" : "item-menu" } onClick={()=>{setLinkActivo(true)}}>Experiencias</a>
                <a className={linkActivo? "item-menu activo" : "item-menu" } onClick={()=>{setLinkActivo(true)}}>Contactanos</a>
        </div>
    );
}
export default Menu;
