import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';
import '../Hojas de Estilos/Contacto.css'
function Contacto(){
    const form = useRef();
    const [validarNombre, setValidarNombre] = useState(false);
    const [validarMail, setValidarMail] = useState(false);
    const [validarMensaje, setValidarMensaje] = useState(false);
    const sendEmail = (e) => {
        e.preventDefault();
        const Nombre = document.getElementById('nombreFormulario');
        const Mail = document.getElementById('mailFormulario');
        const Mensaje = document.getElementById('mensajeFormulario');
        const regexNombre = /^[A-Za-zÁ-Úá-úüÜ]+(?: [A-Za-zÁ-Úá-úüÜ]+)?$/;
        if(regexNombre.test(Nombre.value)){
            setValidarNombre(true);
        }else{
            setValidarNombre(false); 
        }
        if(Mail.value!=""){
            setValidarMail(true);
        }else{
            setValidarMail(false); 
        }
        if(Mensaje.value!=""){
            setValidarMensaje(true);
        }else{
            setValidarMensaje(false); 
        }
        if(validarNombre && validarMail && validarMensaje){
            emailjs.sendForm('service_83ah46g', 'template_d30o58f', form.current, 'YlUpGvdEXIUleFLci')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
        }
        
      };
    return(
        <footer className="seccion-contacto" id='contactanos'>
            <form ref={form} onSubmit={sendEmail} className='formulario'>
                <h1 className='seccion-contacto-titulo'>Contactanos !</h1>
                <label className='item-formulario label-formulario'>Nombre</label>
                <input className='item-formulario input-formulario' type="text" name="user_name" placeholder='Ingresa tu nombre' id='nombreFormulario'/>
                {validarNombre && <span className='item-formulario alerta-formulario'>*Este campo es obligatorio</span>}
                <label className='item-formulario label-formulario'>Email</label>
                <input className='item-formulario input-formulario' type="email" name="user_email" placeholder='Ingresa tu email' id='mailFormulario'/>
                {validarMail && <span className='item-formulario alerta-formulario'>*Este campo es obligatorio</span>}
                <label className='item-formulario label-formulario'>Mensaje</label>
                <textarea className='item-formulario input-formulario textarea-formulario' name="message" placeholder='Ingresa el mensaje que desees enviar' id='mensajeFormulario'/>
                {validarMensaje && <span className='item-formulario alerta-formulario'>*Este campo es obligatorio</span>}
                <input className='item-formulario boton-enviar-formulario' type="submit" value="Enviar Mensaje" />
            </form>
            <div className='contenedor-copyright'>
                <p className="copyright-texto">© copyright 2024</p>
            </div>
            
        </footer>
    );
}
export default Contacto;
