import {React, useState} from "react";
import { isMobile } from 'react-device-detect';
import Estilo from "../Hojas de Estilos/Menu.css";

import ItemMenu from "./ItemMenu";
function Menu({ activo }){
        const definirMenu = ()=>{
            let clase;
            if(isMobile){
                clase=activo ? "menu activo" : "menu";
            }else{
                clase="menu laptop";
            }
            return clase;
        }
        const links= [{Nombre:"Inicio", Activo : false},
            {Nombre:"Sobre Nosotros", Activo : false},
            {Nombre:"Experiencias",Activo: false},
            {Nombre:"Contactanos",Activo: false}]
            
        
    return(
        <div className={definirMenu()}>
             { {
                for(link : links){
                  return(
                  <ItemMenu>
                    {link.Nombre}
                    </ItemMenu>);  
               }
            }
               
             }   
        </div>
    );
}
export default Menu;
