import {React, useState, useEffect} from "react";
import { isMobile } from 'react-device-detect';
import Estilo from "../Hojas de Estilos/Menu.css";

import ItemMenu from "./ItemMenu";
function Menu({ activo }){
    const [scrolling, setScrolling] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
        if (window.scrollY > 0) {
            setScrolling(true);
        } else {
            setScrolling(false);
        }
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
        window.removeEventListener("scroll", handleScroll);
        };
    }, []);
        const definirMenu = ()=>{
            let clase;
            if(isMobile){
                clase=activo ? "menu activo" : "menu";
            }else{
                clase="menu laptop";
            }
            if(scrolling) clase += " fondo-blanco";
            return clase;
        }
        const [links, setLinks] = useState([
            { Nombre: "Inicio", Activo: false },
            { Nombre: "Sobre Nosotros", Activo: false },
            { Nombre: "Experiencias", Activo: false },
            { Nombre: "Contactanos", Activo: false }
          ]);
        const handleLinkClick = (linkIndex) => {
            const actualizarLinks = links.map((link, index) => {
              if (index === linkIndex) {
                return { ...link, Activo: true };
              } else {
                return { ...link, Activo: false };
              }
            });
        
            setLinks(actualizarLinks);
        };
    return(
        <div className={definirMenu()}>
            {links.map((link, index) => (
                <ItemMenu
                key={link.Nombre}
                link={link}
                setActivo={() => handleLinkClick(index)}
               />
            ))}
        </div>
    );
}
export default Menu;
